import React from "react";
var headStyle = {
  border: "3px solid black",
  height: "50px",
  "background-color": "lightgray",
  "text-align": "center"
};
var loginStyle = {
  "background-color": "blue",
  "border-radius": "20px",
  height: "50%",
  width: "50%",
  left: "25%",
  top: "25%",
  position: "absolute",
  "text-align": "center",
  padding: "20px",
  "background-color": "lightgray",
  "background-color": "rgba(0,255,0,0.3)"
};
var usernameStyle = {
  "border-radius": "15px",
  height: "30px",
  "text-align": "center"
};
var passwordStyle = {
  "border-radius": "15px",
  marginTop: "25px",
  height: "30px",
  "text-align": "center"
};
var submitStyle = {
  width: "200px",
  marginTop: "25px",
  height: "30px",
  "background-color": "forestgreen",
  "font-size": "15px"
};

class PrimaPagina extends React.Component {
  render() {
    return (
      <div style={headStyle}>
        <div style={{ "font-size": "40px" }}>Messenger</div>
        <div style={loginStyle}>
          <div style={{ color: "coral" }}>Welcome to my messenger app </div>
          <br />
          <input style={usernameStyle} placeholder="Username" />
          <br />
          <input type="password" style={passwordStyle} placeholder="Password" />
          <br />
          <button onClick={this.props.afterSubmit} style={submitStyle}>
            Submit
          </button>
          <br />
          <button onClick={this.props.swapForm} style={submitStyle}>
            To register
          </button>
        </div>
      </div>
    );
  }
}
export default PrimaPagina;
