import React from "react";
import PrimaPagina from "../PrimaPagina/PrimaPagina";
import "../.././App.css";
import axios from "axios";
var messagesStyle = {
  border: "3px solid coral",
  height: "85%",
  width: "100%",
  "box-sizing": "border-box",
  display: "flex",
  "flex-direction": "column-reverse"
};
var spanStyle = {
  color: "red",
  "align-self": "start",
  "justify-content": "space-around"
};

var convListStyle = {
  display: "inline-block",
  border: "3px solid black",
  "border-radius": "20px",
  height: "100%",
  width: "60%",
  left: "10px",
  "box-sizing": "border-box",
  "background-image":
    "linear-gradient(    141deg, #9fb8ad 0%,#1fc8db 51%,#2cb5e8 75%)    )",
  float: "left",
  overflow: "auto"
};
var divStyle = {
  display: "inline-block",
  "border-bottom": "3px solid black",
  "border-radius": "20px",
  height: "100%",
  width: "20%",
  left: "10px",
  "box-sizing": "border-box",
  "background-image":
    "linear-gradient(    141deg, #9fb8ad 0%,#1fc8db 51%,#2cb5e8 75%)    )",
  overflow: "auto",
  float: "left"
};
var textStyle = {
  "text-align": "center",
  color: "lightgray",
  "box-sizing": "border-box",
  height: "10%",
  "font-size": "32px"
};
var headerStyle = {
  height: "15%",
  border: "3px solid black",
  position: "relative",
  "box-sizing": "border-box",
  "background-color": "rgba(0,0,255,0.3)"
};
var buttonStyle = {
  width: "100px",
  height: "100%",
  position: "absolute",
  right: "0",
  "background-color": "aqua",
  color: "red",
  "font-size": "30px",
  "box-sizing": "border-box"
};
var divimgStyle = {
  height: "95%",
  width: "30%",
  "box-sizing": "border-box",
  "border-radius": "50%"
};

class Submit extends React.Component {
  constructor(props) {
    
    super(props);

    this.state = {
      primapagina : false,
      users : [
        {
          username : "",
          password : "",
          confirmareparola : "",
          link_poza : "",
          email : "",
          nr_telefon : "",
        }
        
      ],

      requests : [
        {
          fromWho : "",
          picture_url : "",
        }
      ]
      
    };
  }
  returnHome = () => {
    this.setState({
      primapagina : true
    })
  }
  
  render() {
    if(this.state.primapagina === true)
    return(
      <PrimaPagina/>
    )
    else {
    return (
      <div
        style={{ height: "100%", width: "100%", "box-sizing": "border-box" }}
      >
        <div style={headerStyle}>
        <button id = "home" onClick = {this.returnHome}>Back to home</button>
          <button onClick = {this.returnHome} style={buttonStyle}>Log out</button>
        </div>
        <div
          style={{ height: "85%", width: "100%", "box-sizing": "border-box" }}
        >
          <div style={divStyle}>
            <div style={textStyle}>Lista de mesaje + cereri</div>
            <div>
            {
              this.state.requests.map(x => (
                <div>
                  <img style = {divimgStyle} src = {x.picture_url} />
                  <div style = {spanStyle}>{x.fromWho}</div>
                </div>
              ))
            }

            </div>
            <div
              style={{
                width: "100%",
                display: "flex",
                "flex-direction": "column"
              }}
            >
              {this.state.users.map(x =>
                
              (
                <div id="img">
                  <img style={divimgStyle} src={x.link_poza} />
                  <div style={spanStyle}>{x.username}</div>
                </div>
              ))}
            </div>
          </div>
          <div style={convListStyle}>
            <div style={textStyle}>Mesajele</div>
            <div style={messagesStyle}>
              <div className="message" style={{ marginLeft: "auto" }}>
                Sal1
              </div>
              <div className="message" style={{ marginRight: "auto" }}>
                Sal2
              </div>
              <div className="message" style={{ marginLeft: "auto" }}>
                Sal3
              </div>
              <div className="message" style={{ marginRight: "auto" }}>
                Sal4
              </div>
              <div className="message" style={{ marginLeft: "auto" }}>
                Sal5
              </div>
              <div className="message" style={{ marginRight: "auto" }}>
                Sal6
              </div>
              <div className="message" style = {{marginLeft: "auto"}}>
                Sal7
              </div>
              <div className="message" style={{ marginRight: "auto" }}>
                Sal8
              </div>
            </div>
            <input id = "writingplace" placeholder="Type here" />
            <button id = "send">Send</button>
            <div />
          </div>
          <div style={divStyle}>
            <div style={textStyle}>Date User</div>
          </div>
        </div>
      </div>
    );
  }
  }
}

export default Submit;
