import React from "react";
import axios from "axios";
import Submit from "../Submit/Submit";
import Register from "../Register/Register";
var headStyle = {
  border: "3px solid black",
  height: "50px",
  "background-color": "lightgray",
  "text-align": "center"
};
var loginStyle = {
  "background-color": "blue",
  "border-radius": "20px",
  height: "50%",
  width: "50%",
  left: "25%",
  top: "25%",
  position: "absolute",
  "text-align": "center",
  padding: "20px",
  "background-color": "lightgray",
  "background-color": "rgba(0,255,0,0.3)"
};
var usernameStyle = {
  "border-radius": "15px",
  height: "30px",
  "text-align": "center"
};
var passwordStyle = {
  "border-radius": "15px",
  marginTop: "25px",
  height: "30px",
  "text-align": "center"
};
var submitStyle = {
  width: "200px",
  marginTop: "25px",
  height: "30px",
  "background-color": "forestgreen",
  "font-size": "15px"
};

class PrimaPagina extends React.Component {
  constructor()
  {
    super();
    this.state = {
      username : "",
      password : "",
      isDataloaded : false,
      data : null,
      dataok : false,
      register : false,
      info : null
    }
  }
  // loadData = () => {
  //   axios.get("http://localhost:3005/users")
  //   .then(req => {
  //     this.setState({
  //       isDataloaded : true,
  //       data : req.data
  //     })
  //   })
  // }
  // componentDidMount() {
  //   this.loadData();
  //   this.setState({
  //     timer : setInterval(this.loadData, 500)
  //   })
  // }
  // componentWillUnmount() {
  //   clearInterval(this.state.timer);
  // }
  uploadData = (e) => {
    this.setState({
      [e.target.id] : e.target.value
    })
  }
  swapForm = () => {
    this.setState({
      register : true
    })
  }
  newlogin = () => {
    if(this.state.username === undefined || this.state.password === undefined)
    alert("Unul dintre campuri nu este valid");
    else if(this.state.username === "" || this.state.password === "")
    alert("Unul dintre campuri este gol");
    else {
      axios.post("http://localhost:3005/login", {
        username : this.state.username,
        password : this.state.password,
      })
      .then(res => {
        //this.loadData();
        if(res.status === 200)
        {
          this.setState({
            dataok : true
          })
          window.localStorage.setItem("cheisecreta",res.data.token);
        }
      })
      .catch(err => {
        alert("Nume sau parola gresite");
        console.log(err);
      })
    }
  }
  render() {
    if(this.state.register === true)
    return(
      <Register/>
    )
    else if(this.state.dataok === true)
    {
      return(
        <Submit/>
      )
    }
    else {
    return (
      <div style={headStyle}>
        <div style={{ "font-size": "40px" }}>Messenger</div>
        <div style={loginStyle}>
          <div style={{ color: "coral" }}>Welcome to my messenger app </div>
          <br />
          <input onChange = {this.uploadData} id = "username" style={usernameStyle} placeholder="Username" />
          <br />
          <input onChange = {this.uploadData} id = "password" type="password" style={passwordStyle} placeholder="Password" />
          <br />
          <button onClick={this.newlogin} style={submitStyle}>
            Submit
          </button>
          <br />
          <button onClick={this.swapForm} style={submitStyle}>
            To register
          </button>
        </div>
      </div>
    );
  }
}
}
export default PrimaPagina;
