import React from "react";
import ReactDOM from "react-dom";
import PrimaPagina from "./Componente/PrimaPagina/PrimaPagina";
import Register from "./Componente/Register/Register";
import Submit from "./Componente/Submit/Submit";

import "./App.css";

class App extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      login: false,
      submit: false,
      register: false
    };
  }

  swapForm = () => {
    this.setState({
      register: !this.state.register
    });
  };

  afterSubmit = () => {
    this.setState({
      login: !this.state.login
    });
  };

  render() {
    if (this.state.login === false)
      return (
        <PrimaPagina afterSubmit={this.afterSubmit} swapForm={this.swapForm} />
      );
    else if (this.state.register) return (<Register  />);
    else if (this.state.login === true) return <Submit swapForm = {this.swapForm}  />;
  }
}

const rootElement = document.getElementById("root");
ReactDOM.render(<App />, rootElement);
